import { Component } from '@angular/core';

@Component({
  selector: 'app-pedido',
  templateUrl: './pedido.component.html',
  styleUrls: ['./pedido.component.css']
})
interface itemCarrinho {
  tipo: string,
  qt: number,
  subtotal: number,
}
export class PedidoComponent {

  carrinho: itemCarrinho[] = [
    { tipo: 'Bermuda', qt: 4, subtotal: 80 },
    { tipo: 'Short', qt: 4, subtotal: 80 },
    { tipo: 'Jeans', qt: 4, subtotal: 80 },
    { tipo: 'Sapato', qt: 4, subtotal: 80 }
  ]
  constructor() {
  }

}
