import { Component, Input, OnInit } from '@angular/core';
interface itemList {
  tipo: string,
  valor_un: number,
  categoria: string
}
@Component({
  selector: 'app-categoria',
  templateUrl: './categoria.component.html',
  styleUrls: ['./categoria.component.css']
})

export class CategoriaComponent implements OnInit {
  @Input() categoria = '';
  @Input() items: itemList[] = [];
  lista_de_ativos: boolean[] = [false, false, false, false]
  constructor() {

  }
  ngOnInit(): void {
    for (let item of this.items) {
      console.log(item.categoria == this.categoria);
    }
  }
  switch(index: number) {
    this.lista_de_ativos[index] = !this.lista_de_ativos;
  }
}
